import './App.css';
import React, {useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css'
import 'bootstrap/dist/css/bootstrap.min.css'
import 'bootstrap/dist/js/bootstrap.bundle.min.js'
import { HomePage, LoginPage } from './pages';
import {getAllUser} from "./axios/userAxios"
import ProfileUser from "./pages/ShowUserPage"



function App() {
  const [loginStatus, setLoginStatus] = useState(false)
  const loginHandlerCb = (result) => {
    setLoginStatus(result)
  }

  const [users, setUsers] = useState([])
  const getUserHandler = () => {
        getAllUser((result) => setUsers(result))
    }


  useEffect(() => {
    if(localStorage.getItem('access_token')){
      setLoginStatus(true)
    }
    else{
      setLoginStatus(false)
    }
  },[loginStatus])

  return (
      <div className='main-page container-fluid'>
        {
          <>
          loginStatus?
            <HomePage loginStatus={loginStatus} loginHandlerCb={loginHandlerCb} getUserHandler={getUserHandler}></HomePage>:
            <LoginPage loginHandlerCb={loginHandlerCb}></LoginPage>
          <ProfileUser users={users}></ProfileUser>
          </>
        }
      </div>
  );
}

export default App;
